<script type="text/javascript">
$('#filterbutton').click(function () {$("#table tbody tr").each(function() {var ck1 = $.trim( $(this).find("#shell_country").text().toLowerCase() );var ck2 = $.trim( $(this).find("#shell_tld").text().toLowerCase() );var ck3 = $.trim( $(this).find("#shell_https").text().toLowerCase() );var ck4 = $.trim( $(this).find("#shell_info").text().toLowerCase() );var ck5 = $.trim( $(this).find("#shell_os").text().toLowerCase() );var ck6 = $.trim( $(this).find("#shell_hosting").text().toLowerCase() );var ck7 = $.trim( $(this).find("#shell_seller").text().toLowerCase() ); var val1 = $.trim( $('select[name="shell_country"]').val().toLowerCase() );var val2 = $.trim( $('input[name="shell_tld"]').val().toLowerCase() );var val3 = $.trim( $('select[name="shell_https"]').val().toLowerCase() );var val4 = $.trim( $('input[name="shell_info"]').val().toLowerCase() );var val5 = $.trim( $('select[name="shell_os"]').val().toLowerCase() );var val6 = $.trim( $('input[name="shell_hosting"]').val().toLowerCase() );var val7 = $.trim( $('select[name="shell_seller"]').val().toLowerCase() ); if((ck1 != val1 && val1 != '' ) || ck2.indexOf(val2)==-1 || (ck3 != val3 && val3 != '' ) || ck4.indexOf(val4)==-1 || (ck5 != val5 && val5 != '' ) || ck6.indexOf(val6)==-1 || (ck7 != val7 && val7 != '' )){ $(this).hide();  }else{ $(this).show(); } });$('#filterbutton').prop('disabled', true);});$('.filterselect').change(function () {$('#filterbutton').prop('disabled', false);});$('.filterinput').keyup(function () {$('#filterbutton').prop('disabled', false);});
function buyit(id,code,price){
  bootbox.confirm("Are you sure?", function(result) {
        if(result ==true){

          balance = $('#balance').text();
          if (price <= balance){
              $("#buy_"+id).html('Purchasing...').show();
              $.ajax({
                type:       'GET',
                url:        'shellbuy'+id+'-'+code+'.html',
                success:    function(data)
                {
                  $("#buy_"+id).html(data).show();
                  ajaxinfo();
                }});
          }
          else {
            bootbox.alert('<center><img src="files/img/balance.png"><h2><b>No enough balance !</b></h2><h4>Please refill your balance <a class="btn btn-primary btn-xs"  href="addBalance.html" onclick="window.open(this.href);return false;" >Add Balance <span class="glyphicon glyphicon-plus"></span></a></h4></center>', function() {});}}
  });
}
g:xcheck=0;

function check(id,code) {
	//xcheck=0;
  if(xcheck > 4) {
    bootbox.alert("<b>Wait</b> - Other checking operation is being executed!");
  }
  else {
    xcheck++;
    $("#check_"+id).html('Checking...').show();
    $.ajax({
      type:       'GET',
      url:        '/shellcheck'+id+'-'+code+'.html',
      success:    function(data)
      {
        $("#check_"+id).html(data).show();
       xcheck--;
      }
    ,error: function (request, status, error) {$("#check_"+id).html('<span class="label label-default">Unknown</span>').show();xcheck=0;}});
  }
}


</script>
